import React, { useState, useEffect } from 'react';
import axios from '../axios';

const Products = () => {
    const [products, setProducts] = useState([]);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [storeId, setStoreId] = useState('');  // Add state for store ID
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:5000/products');
            setProducts(response.data);
        } catch (error) {
            setMessage('Failed to fetch products');
        }
    };

    const handleAddProduct = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/products', { name, description, price, store_id: storeId });
            setMessage(response.data.message);
            fetchProducts();
        } catch (error) {
            setMessage('Failed to add product: ' + error.response.data.message);
        }
    };

    return (
        <div>
            <h2>Products</h2>
            <form onSubmit={handleAddProduct}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </div>
                <div>
                    <label>Description:</label>
                    <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} required />
                </div>
                <div>
                    <label>Price:</label>
                    <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
                </div>
                <div>
                    <label>Store ID:</label> {/* Input for store ID */}
                    <input type="text" value={storeId} onChange={(e) => setStoreId(e.target.value)} required />
                </div>
                <button type="submit">Add Product</button>
            </form>
            <p>{message}</p>
            <h3>Product List</h3>
            <ul>
                {products.map((product) => (
                    <li key={product.id}>
                        {product.name} - {product.description} - ${product.price}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Products;
