import React, { useState, useEffect } from 'react';
import axios from '../axios';

const Stores = () => {
    const [stores, setStores] = useState([]);
    const [name, setName] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchStores();
    }, []);

    const fetchStores = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:5000/stores');
            setStores(response.data);
        } catch (error) {
            setMessage('Failed to fetch stores');
        }
    };

    const handleAddStore = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/stores', { name });
            setMessage(response.data.message);
            fetchStores();
        } catch (error) {
            setMessage('Failed to add store: ' + error.response.data.message);
        }
    };

    return (
        <div>
            <h2>Stores</h2>
            <form onSubmit={handleAddStore}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </div>
                <button type="submit">Add Store</button>
            </form>
            <p>{message}</p>
            <h3>Store List</h3>
            <ul>
                {stores.map((store) => (
                    <li key={store.id}>{store.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default Stores;
