import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://127.0.0.1:5000/login', {
        email,
        password
      });

      console.log('API response:', response); // Log the response object

      if (response.data) {
        // Handle successful login here
        console.log('Login successful:', response.data);
      } else {
        setError('Login failed: No data in response');
      }
    } catch (err) {
      console.log('Error occurred:', err); // Log the error object

      if (err.response) {
        // Server responded with a status other than 2xx
        console.log('Server response:', err.response); // Log the server response
        setError(`Login failed: ${err.response.data.message}`);
      } else if (err.request) {
        // Request was made but no response received
        console.log('No response received:', err.request); // Log the request object
        setError('Login failed: No response from server');
      } else {
        // Something else happened while setting up the request
        console.log('Error message:', err.message); // Log the error message
        setError(`Login failed: ${err.message}`);
      }
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
        {error && <div style={{ color: 'red' }}>{error}</div>}
      </form>
    </div>
  );
}

export default Login;
