import React, { useEffect, useState } from 'react';
import axios from '../axios';

const Profile = () => {
  const [profile, setProfile] = useState({});
  const [error, setError] = useState('');

  const fetchUserData = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:5000/profile?user_id=1`);
      setProfile(response.data);
    } catch (error) {
      setError('Failed to fetch user/store data');
    }
  };

  useEffect(() => {
    fetchUserData();
  }, []);

  return (
    <div>
      <h2>Profile</h2>
      {error && <p>{error}</p>}
      <div>
        <p>Username: {profile.username}</p>
        <p>Email: {profile.email}</p>
        {profile.store && (
          <>
            <p>Store Name: {profile.store.name}</p>
            <p>Store Description: {profile.store.description}</p>
          </>
        )}
      </div>
    </div>
  );
};

export default Profile;
