import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [store, setStore] = useState(null);
  const location = useLocation();

  useEffect(() => {
    const userId = new URLSearchParams(location.search).get('user_id');
    const fetchUserData = async () => {
      try {
        const userResponse = await axios.get(`http://127.0.0.1:5000/user/${userId}`);
        setUser(userResponse.data);
        const storeResponse = await axios.get(`http://127.0.0.1:5000/store/${userResponse.data.store_id}`);
        setStore(storeResponse.data);
      } catch (error) {
        console.error('Error fetching user/store data:', error);
      }
    };
    fetchUserData();
  }, [location.search]);

  return (
    <div>
      {user && (
        <>
          <h2>Profile</h2>
          <p>Username: {user.username}</p>
          <p>Email: {user.email}</p>
          {store && (
            <>
              <h3>Store</h3>
              <p>Store Name: {store.name}</p>
              {/* Add more store details and CRUD operations here */}
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Profile;
