import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState({});
  const [store, setStore] = useState({});
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Fetch user profile data
    const userId = localStorage.getItem('userId'); // Retrieve user ID from local storage
    axios.get(`http://127.0.0.1:5000/profile?user_id=${userId}`)
      .then(response => {
        setUser(response.data);
        setStore(response.data.store || {});
      })
      .catch(error => setMessage('Failed to fetch profile data'));
  }, []);

  const handleUpdate = (e) => {
    e.preventDefault();
    const userId = localStorage.getItem('userId'); // Retrieve user ID from local storage
    axios.put('http://127.0.0.1:5000/profile', { user_id: userId, ...user, store })
      .then(response => setMessage(response.data.message))
      .catch(error => setMessage('Failed to update profile'));
  };

  return (
    <div>
      <h2>Profile</h2>
      <form onSubmit={handleUpdate}>
        <div>
          <label>Username:</label>
          <input type="text" value={user.username} onChange={(e) => setUser({ ...user, username: e.target.value })} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" value={user.email} onChange={(e) => setUser({ ...user, email: e.target.value })} />
        </div>
        <div>
          <label>Store Name:</label>
          <input type="text" value={store.name} onChange={(e) => setStore({ ...store, name: e.target.value })} />
        </div>
        <div>
          <label>Store Description:</label>
          <input type="text" value={store.description} onChange={(e) => setStore({ ...store, description: e.target.value })} />
        </div>
        <button type="submit">Update Profile</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default Profile;
