import React, { useState, useEffect } from 'react';
import axios from '../axios';

const Profile = () => {
  const [profile, setProfile] = useState({});
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/profile', {
        params: { user_id: 1 } // Update to use actual user ID
      });
      setProfile(response.data);
    } catch (error) {
      setMessage('Failed to fetch profile');
    }
  };

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put('http://127.0.0.1:5000/profile', profile);
      setMessage(response.data.message);
    } catch (error) {
      setMessage('Failed to update profile');
    }
  };

  return (
    <div>
      <h2>Profile</h2>
      <form onSubmit={handleUpdateProfile}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={profile.username}
            onChange={(e) => setProfile({ ...profile, username: e.target.value })}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={profile.email}
            onChange={(e) => setProfile({ ...profile, email: e.target.value })}
          />
        </div>
        <div>
          <label>Store Name:</label>
          <input
            type="text"
            value={profile.store?.name || ''}
            onChange={(e) => setProfile({ ...profile, store: { ...profile.store, name: e.target.value } })}
          />
        </div>
        <div>
          <label>Store Description:</label>
          <input
            type="text"
            value={profile.store?.description || ''}
            onChange={(e) => setProfile({ ...profile, store: { ...profile.store, description: e.target.value } })}
          />
        </div>
        <button type="submit">Update Profile</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default Profile;
