import React, { useState, useEffect } from 'react';
import axios from '../axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Profile.css';

const Profile = () => {
    const [profile, setProfile] = useState({});
    const [stores, setStores] = useState([]);
    const [products, setProducts] = useState({});
    const [message, setMessage] = useState('');
    const [editing, setEditing] = useState(false);
    const [storeName, setStoreName] = useState('');
    const [editMode, setEditMode] = useState(false);
    const [updatedProfile, setUpdatedProfile] = useState({ username: '', email: '' });
    const navigate = useNavigate();

    useEffect(() => {
        document.body.classList.add('profile-page');
        return () => {
            document.body.classList.remove('profile-page');
        };
    }, []);

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const userId = localStorage.getItem('user_id');
                const response = await axios.get(`/profile?user_id=${userId}`);
                setProfile(response.data);
                setUpdatedProfile(response.data);
            } catch (error) {
                setMessage('Failed to fetch profile');
            }
        };

        const fetchStores = async () => {
            try {
                const userId = localStorage.getItem('user_id');
                const response = await axios.get(`/api/stores?owner_id=${userId}`);
                setStores(response.data);
                setStoreName(response.data[0]?.name || ''); // Set initial store name
            } catch (error) {
                setMessage('Failed to fetch stores');
            }
        };

        fetchProfile();
        fetchStores();
    }, []);

    const fetchProducts = async (storeId) => {
        try {
            const response = await axios.get(`/api/products?store_id=${storeId}`);
            setProducts((prevProducts) => ({
                ...prevProducts,
                [storeId]: response.data,
            }));
        } catch (error) {
            setMessage('Failed to fetch products');
        }
    };

    const handleSelectStore = (store) => {
        localStorage.setItem('store_id', store.id);
        fetchProducts(store.id);
        navigate('/products');
    };

    const handleStoreNameChange = (e) => {
        setStoreName(e.target.value);
    };

    const handleStoreNameEdit = () => {
        setEditing(!editing);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedProfile({ ...updatedProfile, [name]: value });
    };

    const handleUpdateProfile = async () => {
        try {
            const userId = localStorage.getItem('user_id');
            await axios.put(`/profile/${userId}`, updatedProfile);
            setProfile(updatedProfile);
            setEditMode(false);
            setMessage('Profile updated successfully');
            setTimeout(() => {
                setMessage('');
            }, 3000);
        } catch (err) {
            console.error('Error updating profile:', err);
            setMessage('Failed to update profile');
        }
    };

    const handleDeleteProfile = async () => {
        try {
            const userId = localStorage.getItem('user_id');
            await axios.delete(`/profile/${userId}`);
            localStorage.removeItem('token');
            localStorage.removeItem('user_id');
            window.location.href = '/login';
        } catch (err) {
            console.error('Error deleting profile:', err);
        }
    };

    return (
        <div className="profile-container">
            <div className="welcome-box">
                <img src="/images/profile.png" alt="Avatar" className="avatar" onClick={() => setEditMode(!editMode)}/>
                <h3>Hello, {profile.username || 'Name'}!</h3>
            </div>
            {editMode && (
                <div className="profile-crud">
                    <div className="form-group">
                        <label>Username:</label>
                        <input
                            type="text"
                            name="username"
                            value={updatedProfile.username}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value={updatedProfile.email}
                            onChange={handleInputChange}
                        />
                    </div>
                    <button className="update-button" onClick={handleUpdateProfile}>
                        Update Profile
                    </button>
                    <button className="delete-button" onClick={handleDeleteProfile}>
                        Delete Profile
                    </button>
                </div>
            )}
            {!editMode && (
                <div className="store-box">
                    <div className="store-image">
                        {editing ? (
                            <input 
                                type="text" 
                                value={storeName} 
                                onChange={handleStoreNameChange} 
                                onBlur={handleStoreNameEdit} 
                                autoFocus
                            />
                        ) : (
                            <h4 onClick={handleStoreNameEdit}>{storeName}</h4>
                        )}
                    </div>
                </div>
            )}
            <div className="navigation-box">
                <div className="nav-icon">
                    <img src="/images/order.png" alt="Products"/>
                </div>
                <div className="nav-icon">
                    <img src="/images/myorders.png" alt="My Orders"/>
                </div>
                <div className="nav-icon">
                    <img src="/images/logout.png" alt="Logout"/>
                </div>
            </div>
            {stores.length > 0 && (
                stores.map((store) => (
                    <div key={store.id}>
                        <h4>{store.name}</h4>
                        <p>{store.description}</p>
                        <button onClick={() => handleSelectStore(store)}>View Products</button>
                        <ul>
                            {products[store.id] && products[store.id].map((product) => (
                                <li key={product.id}>
                                    {product.name} - {product.description} - ${product.price} - {product.unit}
                                    <button>Edit</button>
                                    <button>Delete</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            )}
            {message && <p className="message">{message}</p>}
        </div>
    );
};

export default Profile;
