import React, { useState, useEffect } from 'react';
import axios from '../axios';
import { useNavigate, Link } from 'react-router-dom';

const Stores = () => {
    const [stores, setStores] = useState([]);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    // Fetch stores when the component mounts
    useEffect(() => {
        const fetchStores = async () => {
            try {
                const userId = localStorage.getItem('user_id');
                const response = await axios.get(`/api/stores?owner_id=${userId}`);
                setStores(response.data);
            } catch (error) {
                setMessage('Failed to fetch stores');
            }
        };

        fetchStores();
    }, []);

    // Handle the addition of a new store
    const handleAddStore = async (e) => {
        e.preventDefault();
        try {
            const userId = localStorage.getItem('user_id');
            const response = await axios.post('/api/stores', { name, description, owner_id: userId });
            setMessage(response.data.message);
            setStores([...stores, response.data.store]); // Add the new store to the list
            setName('');
            setDescription('');
            localStorage.setItem('store_id', response.data.store.id); // Update store_id in localStorage
            navigate('/products'); // Redirect to products page
        } catch (error) {
            setMessage('Failed to add store: ' + (error.response.data.message || error.message));
        }
    };

    return (
        <div>
            <h2>Stores</h2>
            {/* Form to add a new store */}
            <form onSubmit={handleAddStore}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </div>
                <div>
                    <label>Description:</label>
                    <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} required />
                </div>
                <button type="submit">Add Store</button>
            </form>
            <p>{message}</p>
            <h3>Store List</h3>
            {/* List of stores */}
            <ul>
                {stores.map((store) => (
                    <li key={store.id}>
                        {store.name} - <Link to={`/storefront/${store.id}`}>View Storefront</Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Stores;
