import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Profile.css'; // Import the CSS file to apply the styles globally

const NavBar = ({ children }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div>
      {children}
      <div className="navigation-box">
        <div className="nav-icon" onClick={() => navigate('/profile')}>
          <img src="/images/profile.png" alt="Profile" />
        </div>
        <div className="nav-icon" onClick={() => navigate('/stores')}>
          <img src="/images/order.png" alt="Stores" />
        </div>
        <div className="nav-icon" onClick={() => navigate('/products')}>
          <img src="/images/myorders.png" alt="Products" />
        </div>
        <div className="nav-icon" onClick={handleLogout}>
          <img src="/images/logout.png" alt="Logout" />
        </div>
      </div>
    </div>
  );
};

export default NavBar;
