import React, { useState, useEffect } from 'react';
import axios from '../axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Profile.css';

const Profile = () => {
    const [profile, setProfile] = useState({});
    const [stores, setStores] = useState([]);
    const [products, setProducts] = useState({});
    const [message, setMessage] = useState('');
    const [editing, setEditing] = useState(false);
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [storeName, setStoreName] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const userId = localStorage.getItem('user_id');
                const response = await axios.get(`/profile?user_id=${userId}`);
                setProfile(response.data);
                setUsername(response.data.username);
                setEmail(response.data.email);
            } catch (error) {
                setMessage('Failed to fetch profile');
            }
        };

        const fetchStores = async () => {
            try {
                const userId = localStorage.getItem('user_id');
                const response = await axios.get(`/api/stores?owner_id=${userId}`);
                setStores(response.data);
                setStoreName(response.data[0]?.name || ''); // Set initial store name
            } catch (error) {
                setMessage('Failed to fetch stores');
            }
        };

        fetchProfile();
        fetchStores();
    }, []);

    const fetchProducts = async (storeId) => {
        try {
            const response = await axios.get(`/api/products?store_id=${storeId}`);
            setProducts((prevProducts) => ({
                ...prevProducts,
                [storeId]: response.data,
            }));
        } catch (error) {
            setMessage('Failed to fetch products');
        }
    };

    const handleSelectStore = (store) => {
        localStorage.setItem('store_id', store.id);
        fetchProducts(store.id);
        navigate('/products');
    };

    const handleStoreNameChange = (e) => {
        setStoreName(e.target.value);
    };

    const handleStoreNameEdit = () => {
        setEditing(!editing);
    };

    const handleUpdateProfile = async (e) => {
        e.preventDefault();
        try {
            const userId = localStorage.getItem('user_id');
            const response = await axios.put(`/profile/${userId}`, { username, email });
            setMessage('Profile updated successfully');
            setProfile(response.data.profile);
            setEditing(false);
        } catch (error) {
            console.log('Error updating profile:', error);
            setMessage(error.response?.data?.message || 'An error occurred while updating the profile');
        }
    };

    const handleDeleteProfile = async () => {
        try {
            const userId = localStorage.getItem('user_id');
            await axios.delete(`/profile/${userId}`);
            setMessage('Profile deleted successfully');
            localStorage.removeItem('token');
            localStorage.removeItem('user_id');
            navigate('/login');
        } catch (error) {
            console.log('Error deleting profile:', error);
            setMessage(error.response?.data?.message || 'An error occurred while deleting the profile');
        }
    };

    return (
        <div className="profile-container">
            <div className="welcome-box">
                <img src="/images/profile.png" alt="Avatar" className="avatar" />
                <h3>Hello, {profile.username || 'Name'}!</h3>
            </div>
            <div className="store-box">
                {editing ? (
                    <form onSubmit={handleUpdateProfile}>
                        <div className="form-group">
                            <label htmlFor="username">Username:</label>
                            <input
                                type="text"
                                id="username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email:</label>
                            <input
                                type="email"
                                id="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <button type="submit" className="update-button">Update Profile</button>
                    </form>
                ) : (
                    <div>
                        <p>Username: {profile.username}</p>
                        <p>Email: {profile.email}</p>
                        <button onClick={() => setEditing(true)} className="edit-button">Edit Profile</button>
                    </div>
                )}
                <button onClick={handleDeleteProfile} className="delete-button">Delete Profile</button>
                {message && <p className="message">{message}</p>}
            </div>
            {stores.length > 0 && (
                stores.map((store) => (
                    <div key={store.id}>
                        <h4>{store.name}</h4>
                        <p>{store.description}</p>
                        <button onClick={() => handleSelectStore(store)}>View Products</button>
                        <ul>
                            {products[store.id] && products[store.id].map((product) => (
                                <li key={product.id}>
                                    {product.name} - {product.description} - ${product.price} - {product.unit}
                                    <button>Edit</button>
                                    <button>Delete</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            )}
            {message && <p className="message">{message}</p>}
        </div>
    );
};

export default Profile;
